<div class="layui-card layui-bg-gray"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header notselect"><span class="layui-icon layui-icon-next font-s10 color-desc margin-right-5"></span><?php echo htmlentities((isset($title) && ($title !== '')?$title:'')); ?><div class="pull-right"><!--<?php if(auth("add")): ?>--><button data-modal='<?php echo url("add"); ?>' data-title="添加权限" class='layui-btn layui-btn-sm layui-btn-primary'>添加权限</button><!--<?php endif; ?>--><!--<?php if(auth("remove")): ?>--><button data-action='<?php echo url("remove"); ?>' data-rule="id#{key}" data-csrf="<?php echo systoken('remove'); ?>" data-confirm="确定要删除这些权限吗？" class='layui-btn layui-btn-sm layui-btn-primary'>删除权限</button><!--<?php endif; ?>--></div></div><?php endif; ?><div class="layui-card-body"><div class="think-box-shadow table-block"><fieldset><legend>条件搜索</legend><form class="layui-form layui-form-pane form-search" action="<?php echo request()->url(); ?>" onsubmit="return false" method="get" autocomplete="off"><div class="layui-form-item layui-inline"><label class="layui-form-label">权限名称</label><div class="layui-input-inline"><input name="title" value="<?php echo input('get.title'); ?>" placeholder="请输入权限名称" class="layui-input"></div></div><div class="layui-form-item layui-inline"><label class="layui-form-label">权限描述</label><div class="layui-input-inline"><input name="desc" value="<?php echo input('get.desc'); ?>" placeholder="请输入权限描述" class="layui-input"></div></div><div class="layui-form-item layui-inline"><label class="layui-form-label">使用状态</label><div class="layui-input-inline"><select class="layui-select" name="status"><?php foreach([''=>'-- 全部状态 --','0'=>'已禁用的权限','1'=>'使用中的权限'] as $k=>$v): if(input('get.status') == $k.""): ?><option selected value="<?php echo htmlentities($k); ?>"><?php echo htmlentities($v); ?></option><?php else: ?><option value="<?php echo htmlentities($k); ?>"><?php echo htmlentities($v); ?></option><?php endif; ?><?php endforeach; ?></select></div></div><div class="layui-form-item layui-inline"><label class="layui-form-label">创建时间</label><div class="layui-input-inline"><input data-date-range name="create_at" value="<?php echo input('get.create_at'); ?>" placeholder="请选择创建时间" class="layui-input"></div></div><div class="layui-form-item layui-inline"><button class="layui-btn layui-btn-primary"><i class="layui-icon">&#xe615;</i> 搜 索</button></div></form><script>form.render()</script></fieldset><table class="layui-table margin-top-10" lay-skin="line"><?php if(!(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty()))): ?><thead><tr><th class='list-table-check-td think-checkbox'><label><input data-auto-none data-check-target='.list-check-box' type='checkbox'></label></th><th class='text-left nowrap'>权限名称</th><th class='text-left nowrap'>权限描述</th><th class='text-left nowrap'>创建时间</th><th class="text-center nowrap">使用状态</th><th></th></tr></thead><?php endif; ?><tbody><?php foreach($list as $key => $vo): ?><tr data-dbclick><td class='list-table-check-td think-checkbox'><label><input class="list-check-box" value='<?php echo htmlentities($vo['id']); ?>' type='checkbox'></label></td><td class='text-left'><?php echo htmlentities((isset($vo['title']) && ($vo['title'] !== '')?$vo['title']:'-')); ?></td><td class='text-left'><?php echo htmlentities((isset($vo['desc']) && ($vo['desc'] !== '')?$vo['desc']:"没有写描述哦！")); ?></td><td class="text-left nowrap"><?php echo htmlentities(format_datetime($vo['create_at'])); ?></td><td class='text-center nowrap'><?php if($vo['status'] == '0'): ?><span class="color-red">已禁用</span><?php else: ?><span class="color-green">使用中</span><?php endif; ?></td><td class='text-center nowrap'><!--<?php if(auth("edit")): ?>--><a data-dbclick class="layui-btn layui-btn-xs" data-title="编辑权限" data-modal='<?php echo url("edit"); ?>?id=<?php echo htmlentities($vo['id']); ?>'>编 辑</a><!--<?php endif; ?>--><!--<?php if(auth("apply")): ?>--><a class="layui-btn layui-btn-normal layui-btn-xs" data-open='<?php echo url("apply"); ?>?id=<?php echo htmlentities($vo['id']); ?>'>授 权</a><!--<?php endif; ?>--><!--<?php if($vo['status'] == 1 and auth("state")): ?>--><a class="layui-btn layui-btn-warm layui-btn-xs" data-confirm="确定禁用该权限吗？" data-action="<?php echo url('state'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>;status#0" data-csrf="<?php echo systoken('state'); ?>">禁 用</a><!--<?php endif; ?>--><!--<?php if($vo['status'] == 0 and auth("state")): ?>--><a class="layui-btn layui-btn-warm layui-btn-xs" data-confirm="确定启用该权限吗？" data-action="<?php echo url('state'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>;status#1" data-csrf="<?php echo systoken('state'); ?>">启 用</a><!--<?php endif; ?>--><!--<?php if(auth("remove")): ?>--><a class="layui-btn layui-btn-danger layui-btn-xs" data-confirm="确定要删除权限吗?" data-action="<?php echo url('remove'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>" data-csrf="<?php echo systoken('remove'); ?>">删 除</a><!--<?php endif; ?>--></td></tr><?php endforeach; ?></tbody></table><?php if(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty())): ?><span class="notdata">没有记录哦</span><?php else: ?><?php echo (isset($pagehtml) && ($pagehtml !== '')?$pagehtml:''); ?><?php endif; ?></div></div></div>